mapper.select:
SELECT * FROM challenge


      AND status = #{status}


        AND user_id = #{userId}

SELECT EXISTS (
    SELECT 1 FROM challenge WHERE id = #{id}
)

SELECT EXISTS (
    SELECT * FROM challenge WHERE start_dt = #{today}
)

SELECT EXISTS (
         SELECT * FROM challenge WHERE end_dt = #{today}
         )

mapper.update:
UPDATE challenge
SET type = #{type}, start_dt = #{startDt}, end_dt = #{endDt}, goal_cnt = #{goalCnt}
WHERE id = #{id}
