mapper.select:
SELECT EXISTS (
    SELECT 1 FROM game
    WHERE DATE(date) = #{date}
    AND type = #{type}
    AND difficulty_level = #{difficultyLevel}
    AND user_id = #{userId}
    AND is_achieved = true
)

mapper.update:
UPDATE game
SET is_achieved = true
WHERE id = #{id}

mapper.select:
SELECT * FROM game
WHERE user_id = #{userId}
  AND date BETWEEN #{startOfMonth} AND #{endOfMonth}
