mapper.update:
UPDATE user
SET exp = exp + #{expPoints}
WHERE id = #{userId}

UPDATE user
SET nickname = #{nickname}, profile_img = #{profileImg}
WHERE id = #{id}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
FROM user
WHERE nickname = #{nickname}

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
FROM user
WHERE email = #{email}
